    @Override
    public boolean onOptionsItemSelected(MenuItem menuItem) {
        if (menuItem.getItemId() == android.R.id.home) {

            if (activityName.startsWith("Main"))
                MainActivity.drawer.openDrawer(Gravity.START);
            else if (activityName.startsWith("User"))
                UserView.drawer.openDrawer(Gravity.START);
            else if (activityName.startsWith("Admin"))
                AdminView.drawer.openDrawer(Gravity.START);
            else if (activityName.startsWith("Master"))
                MasterAdminView.drawer.openDrawer(Gravity.START);
            return true;
        }
        return false;
    }

    public TextView textHeader(String text) {
        ViewGroup.LayoutParams layoutParams = new ViewGroup.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.WRAP_CONTENT);
        TextView textView = new TextView(getActivity());
        textView.setLayoutParams(layoutParams);
        textView.setText(text);
        textView.setTextColor(getResources().getColor(R.color.pink));
        textView.setTextSize(22);
        textView.setTypeface(textView.getTypeface(), Typeface.BOLD);
        return textView;
    }

    public TextView textField(String text) {
        ViewGroup.LayoutParams layoutParams = new ViewGroup.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.WRAP_CONTENT);
        TextView textView = new TextView(getActivity());
        textView.setLayoutParams(layoutParams);
        textView.setText("\n" + text + "\n");
        textView.setTextColor(getResources().getColor(R.color.white));
        textView.setTextSize(16);
        textView.setLineSpacing(2, 1.25f);
        return textView;
    }

